INSERT INTO `zhihu_answer` VALUES ('196872678', 'http://www.zhihu.com/api/v4/answers/196872678', '62288023', '0', '八十人一定要开除。<br>下次钓鱼请写，登入以后涨工资发股票。<br>星巴克难道不要求一个道歉？<br>狼来了的故事。', '4', '0', '2017-07-11', '2017-07-11', '2017-07-14 19:17:19', null);
