INSERT INTO `zhihu_answer` VALUES ('197598216', 'http://www.zhihu.com/api/v4/answers/197598216', '62355519', '0', '那以后如果出现投诉找平台还是销售员？', '0', '0', '2017-07-13', '2017-07-13', '2017-07-14 19:17:19', null);
INSERT INTO `zhihu_answer` VALUES ('197612344', 'http://www.zhihu.com/api/v4/answers/197612344', '62355519', '0', '这个平台以后可以完全把握住老师的科研项目进展。到时候再把信息卖给其他人。我就呵呵了，中国科研底裤都会被扒掉，赤裸裸的露在外国人面前了。', '0', '0', '2017-07-13', '2017-07-13', '2017-07-14 19:17:19', null);
INSERT INTO `zhihu_answer` VALUES ('197801364', 'http://www.zhihu.com/api/v4/answers/197801364', '62288023', '0', '<p>百度安全部门也常常给自己的员工发钓鱼邮件，误点的员工会被要求学习网络安全的课程，不过只限于内部员工。</p>', '0', '0', '2017-07-13', '2017-07-13', '2017-07-14 19:17:22', null);
