INSERT INTO `zhihu_question` VALUES ('23073812', '黑客 (Hacker),自学,软件安全', 'https://www.zhihu.com/question/23073812', '自学成才的黑客（安全研究员）是从哪学到那些知识的？', '<div class=\"QuestionHeader-detail\"><div class=\"QuestionRichText QuestionRichText--expandable QuestionRichText--collapsed\"><div><span class=\"RichText\" itemprop=\"text\">相关问题：<a href=\"http://www.zhihu.com/question/26677156\" class=\"internal\">想成为网络安全技术爱好者（可能是黑客）的话，需要看什么书？</a> -------- <b>本题已收入知乎圆桌 » </b><a href=\"http://www.zhihu.com/roundtable/white-hat\" class=\"internal\">白帽黑客与安全</a><b>，欢迎关注讨论。</b></span><button class=\"Button QuestionRichText-more Button--plain\" type=\"button\">显示全部<svg viewbox=\"0 0 10 6\" class=\"Icon QuestionRichText-more-icon Icon--arrow\" style=\"height:16px;width:10px;\" width=\"10\" height=\"16\" aria-hidden=\"true\"><title></title><g><path d=\"M8.716.217L5.002 4 1.285.218C.99-.072.514-.072.22.218c-.294.29-.294.76 0 1.052l4.25 4.512c.292.29.77.29 1.063 0L9.78 1.27c.293-.29.293-.76 0-1.052-.295-.29-.77-.29-1.063 0z\"></path></g></svg></button></div></div></div>', null, null, '48', '8', '15117', '568989', '2017-07-14 19:17:18', null);
INSERT INTO `zhihu_question` VALUES ('23332058', '汇编语言,汇编入门书籍', 'https://www.zhihu.com/question/23332058', '汇编入门先看哪本入门书籍？', '<div class=\"QuestionHeader-detail\"><div class=\"QuestionRichText QuestionRichText--expandable QuestionRichText--collapsed\"><div><span class=\"RichText\" itemprop=\"text\">有一点代码基础但是是java应该算是零基础对于汇编来说，想问下零基础先看哪本书入门啊？</span><button class=\"Button QuestionRichText-more Button--plain\" type=\"button\">显示全部<svg viewbox=\"0 0 10 6\" class=\"Icon QuestionRichText-more-icon Icon--arrow\" style=\"height:16px;width:10px;\" width=\"10\" height=\"16\" aria-hidden=\"true\"><title></title><g><path d=\"M8.716.217L5.002 4 1.285.218C.99-.072.514-.072.22.218c-.294.29-.294.76 0 1.052l4.25 4.512c.292.29.77.29 1.063 0L9.78 1.27c.293-.29.293-.76 0-1.052-.295-.29-.77-.29-1.063 0z\"></path></g></svg></button></div></div></div>', null, null, '25', '1', '324', '41581', '2017-07-14 19:17:16', null);
