/span></div><div class=\"crayon-line crayon-striped-line\" id=\"crayon-5962abb11be0b327735076-2\"><span class=\"crayon-p\">#include &lt;fcntl.h&gt;</span></div><div class=\"crayon-line\" id=\"crayon-5962abb11be0b327735076-3\"><span class=\"crayon-p\">#include &lt;sys/stat.h&gt;</span></div><div class=\"crayon-line crayon-striped-line\" id=\"crayon-5962abb11be0b327735076-4\"><span class=\"crayon-p\">#include &lt;sys/types.h&gt;</span></div><div class=\"crayon-line\" id=\"crayon-5962abb11be0b327735076-5\"><span class=\"crayon-p\">#include &lt;sys/mman.h&gt;</span></div><div class=\"crayon-line crayon-striped-line\" id=\"crayon-5962abb11be0b327735076-6\"><span class=\"crayon-p\">#include &lt;stdio.h&gt;</span></div><div class=\"crayon-line\" id=\"crayon-5962abb11be0b327735076-7\"><span class=\"crayon-p\">#include &lt;stdlib.h&gt;</span></div><div class=\"crayon-line crayon-striped-line\" id=\"crayon-5962abb11be0b327735076-8\"><span class=\"crayon-p\">#define MAP_SIZE 68157440</span></div><div class=\"crayon-line\" id=\"crayon-5962abb11be0b327735076-9\"><span class=\"crayon-t\">int</span><span class=\"crayon-h\"> </span><span class=\"crayon-e\">main</span><span class=\"crayon-sy\">(</span><span class=\"crayon-t\">int</span><span class=\"crayon-h\"> </span><span class=\"crayon-v\">argc</span><span class=\"crayon-sy\">,</span><span class=\"crayon-h\"> </span><span class=\"crayon-t\">char</span><span class=\"crayon-h\"> </span><span class=\"crayon-o\">*</span><span class=\"crayon-v\">argv</span><span class=\"crayon-sy\">[</span><span class=\"crayon-sy\">]</span><span class=\"crayon-sy\">)</span></div><div class=\"crayon-line crayon-striped-line\" id=\"crayon-5962abb11be0b327735076-10\"><span class=\"crayon-sy\">{</span></div><div class=\"crayon-line\" id=\"crayon-5962abb11be0b327735076-11\"><span class=\"crayon-h\">    </span><span class=\"crayon-t\">int</span><span class=\"crayon-h\"> </span><span class=\"crayon-v\">fd</span><span class=\"crayon-sy\">;</span></div><div cl